其實唔關完全React hooks 事<img src="/assets/faces/normal/sosad.gif" class="hkgmoji" /><br />
<pre><code data-type="">function useBoy(name) {
  return {
    屌: (girl) =&gt; {
      girl.on屌();
    },
    kiss: (girl) =&gt; {
      girl.onKiss();
    }
  };
}

function useGirl(name) {
  const girl = {
    含撚: (boy) =&gt; {
      boy.kiss(girl);
    },
    on屌: () =&gt; {
      girl.叫(&quot;爽&quot;);
    },
    onKiss: () =&gt; {
    },
    叫: (content) =&gt; {
      console.log(`${name}:「${content}」`);
    }
  }

  return girl;
}

function 開房(props) =&gt; {
  const boy = useBoy(&quot;樓主&quot;);
  const girl = useGirl(&quot;女神&quot;);

  boy.屌(girl);
  girl.含撚(body);

  return null;
}

</code></pre>

