    <br />
    public static int factorial(int number){<br />
        int result = 1;<br />
        if(number &lt;= 1){<br />
            return result;<br />
        }<br />
        result = number * Main.factorial(number - 1); <br />
        return result;<br />
    }<br />
} <br />
