        if (j == A.length) {
            B[i] = A[i];
        } else if (A[i] != A[j]) {
            // 0 1    1 0
            // 1 ? or 0 ?
            B[i] = 1;
        } else if (A[i] == 0) {
            // 0 0
            // 0 ?
            B[i] = 0;
        } else {
            B[i] = 0;
            if (j + 1 == A.length || A[j + 1] == 0) {
                // 1 1
                // 1 0
                B[j] = 0;
            } else {
                // 1 1
                // 1 1
                B[j] = 1;
            }
            i++;
            j++;
        }
    }
    int effectiveIndex = 0;
    for (int i = B.length - 1; i &gt;= 0; i--) {
        if (B[i] == 1) {
            effectiveIndex = i;
            break;
        }
    }
    return Arrays.copyOf(B, effectiveIndex + 1);
}</code></pre><br />
