複製呢 D code 去純文字檔，再 save as .html ，用支援 HTML5 既瀏覽器開啟，可以播到音樂<br />
估下係咩音樂<br />
<code>&lt;!DOCTYPE html&gt;
&lt;html lang=&quot;zh-hk&quot;&gt;
	&lt;head&gt;
		&lt;title&gt;Untitled&lt;/title&gt;
		&lt;meta charset=&quot;UTF-8&quot;/&gt;
		&lt;script&gt;
function getFrequencies(){
	var b = 440;
	var fs = [&quot;A0&quot;, &quot;A0s&quot;, &quot;B0&quot;, &quot;C1&quot;, &quot;C1s&quot;, &quot;D1&quot;, &quot;D1s&quot;, &quot;E1&quot;, &quot;F1&quot;, &quot;F1s&quot;, &quot;G1&quot;, &quot;G1s&quot;, &quot;A1&quot;, &quot;A1s&quot;, &quot;B1&quot;,&quot;C2&quot;, &quot;C2s&quot;, &quot;D2&quot;, &quot;D2s&quot;, &quot;E2&quot;, &quot;F2&quot;, &quot;F2s&quot;, &quot;G2&quot;, &quot;G2s&quot;, &quot;A2&quot;, &quot;A2s&quot;, &quot;B2&quot;, &quot;C3&quot;, &quot;C3s&quot;, &quot;D3&quot;, &quot;D3s&quot;, &quot;E3&quot;, &quot;F3&quot;, &quot;F3s&quot;, &quot;G3&quot;, &quot;G3s&quot;, &quot;A3&quot;, &quot;A3s&quot;, &quot;B3&quot;, &quot;C4&quot;, &quot;C4s&quot;, &quot;D4&quot;, &quot;D4s&quot;, &quot;E4&quot;, &quot;F4&quot;, &quot;F4s&quot;, &quot;G4&quot;, &quot;G4s&quot;, &quot;A4&quot;, &quot;A4s&quot;, &quot;B4&quot;, &quot;C5&quot;, &quot;C5s&quot;, &quot;D5&quot;, &quot;D5s&quot;, &quot;E5&quot;, &quot;F5&quot;, &quot;F5s&quot;, &quot;G5&quot;, &quot;G5s&quot;, &quot;A5&quot;, &quot;A5s&quot;, &quot;B5&quot;, &quot;C6&quot;, &quot;C6s&quot;, &quot;D6&quot;, &quot;D6s&quot;, &quot;E6&quot;, &quot;F6&quot;, &quot;F6s&quot;, &quot;G6&quot;, &quot;G6s&quot;, &quot;A6&quot;, &quot;A6s&quot;, &quot;B6&quot;, &quot;C7&quot;, &quot;C7s&quot;, &quot;D7&quot;, &quot;D7s&quot;, &quot;E7&quot;, &quot;F7&quot;, &quot;F7s&quot;, &quot;G7&quot;, &quot;G7s&quot;, &quot;A7&quot;, &quot;A7s&quot;, &quot;B7&quot;, &quot;C8&quot;];
	var f = {};
	for (var i in fs){
		f[fs[i]] = b * Math.pow(2, (i - 48) / 12);
	}
	return f;
}

function playWave(f, s, d){
	var context = new (AudioContext || webkitAudioContext)();
	var o = context.createOscillator();
	var g = context.createGain();
	g.gain.value = 10 / 100;
	g.connect(context.destination);
	o.type = &quot;sine&quot;;
	o.frequency.value = f;
	o.detune.value = 1000;
	o.connect(g);
	o.start(s);
	o.stop(s + d);
}

function playXmas(){
	var f = getFrequencies();
	var o = 4;
	var s = 0;
	var d;

	d = 2; playWave(f.D4, s / o, d / o); s += d;
	d = 2; playWave(f.G4, s / o, d / o); s += d;
	d = 1; playWave(f.G4, s / o, d / o); s += d;
	d = 1; playWave(f.A4, s / o, d / o); s += d;
	d = 1; playWave(f.G4, s / o, d / o); s += d;
	d = 1; playWave(f.F4s, s / o, d / o); s += d;
	d = 2; playWave(f.E4, s / o, d / o); s += d;
	d = 2; playWave(f.E4, s / o, d / o); s += d;

	d = 2; playWave(f.E4, s / o, d / o); s += d;
	d = 2; playWave(f.A4, s / o, d / o); s += d;
	d = 1; playWave(f.A4, s / o, d / o); s += d;
	d = 1; playWave(f.B4, s / o, d / o); s += d;
	d = 1; playWave(f.A4, s / o, d / o); s += d;
	d = 1; playWave(f.G4, s / o, d / o); s += d;
	d = 2; playWave(f.F4s, s / o, d / o); s += d;
	d = 2; playWave(f.D4, s / o, d / o); s += d;

	d = 2; playWave(f.D4, s / o, d / o); s += d;
	d = 2; playWave(f.B4, s / o, d / o); s += d;
	d = 1; playWave(f.B4, s / o, d / o); s += d;
	d = 1; playWave(f.C5, s / o, d / o); s += d;
	d = 1; playWave(f.B4, s / o, d / o); s += d;
	d = 1; playWave(f.A4, s / o, d / o); s += d;
	d = 2; playWave(f.G4, s / o, d / o); s += d;
	d = 2; playWave(f.E4, s / o, d / o); s += d;

	d = 1; playWave(f.D4, s / o, d / o); s += d;
	d = 1; playWave(f.D4, s / o, d / o); s += d;
	d = 2; playWave(f.E4, s / o, d / o); s += d;
	d = 2; playWave(f.A4, s / o, d / o); s += d;
	d = 2; playWave(f.F4s, s / o, d / o); s += d;
	d = 4; playWave(f.G4, s / o, d / o); s += d;
}
		&lt;/script&gt;
	&lt;/head&gt;
	&lt;body&gt;
		&lt;input type=&quot;button&quot; value=&quot;play Xmas&quot; onclick=&quot;playXmas();&quot;/&gt;
	&lt;/body&gt;
	&lt;/body&gt;
&lt;/html&gt;</code>

